INSERT INTO os_service_bugnote_text_table VALUES (1,'I\'ve recovered the server but it\'s in a million pieces and squirrels were building their nest in it. I doubt there\'s any data left to recover.');
INSERT INTO os_service_bugnote_text_table VALUES (2,'I\'ve just orderd \"Tech yourself database management in 24 minutes\" for Joe. Apparently he was very proud that he had moved from punch cards to the tape drive and is now disappointed that he has to master another one of these \'brand new\' technologies.');
